/* Convierte la estructura de partida en un árbol para d3js */
function makeInfoTree(info) {
	var messages = info.mensajes;
	var index = {};
	var rootMsg = messages[0];
	index[rootMsg.idMsg] = rootMsg;
	var replies = messages.slice(1);
	replies.forEach(function(m) {
		var parentID = m.idMsgRespuesta;
		var parentObj = index[parentID];
		var children = (parentObj.children || (parentObj.children = []));
		children.push(m);
		index[m.idMsg] = m;
	});
	return rootMsg;
};

function drawRadialTree(root) {
	// visualización en árbol:
	var diameter = 960;
	var tree = d3.layout.tree()
		.size([360, diameter / 2 - 120])
		.separation(function(a, b) { return (a.parent == b.parent ? 1 : 2) / a.depth; });

	var diagonal = d3.svg.diagonal.radial()
		.projection(function(d) { return [d.y, d.x / 180 * Math.PI]; });

	var svg = d3.select("body").append("svg")
		.append("g")
		.attr("transform", "translate(" + diameter / 2 + "," + diameter / 2 + ")");

	
	var nodes = tree.nodes(root),
		links = tree.links(nodes);

	var link = svg.selectAll(".link")
		.data(links)
		.enter().append("path")
		.attr("class", "link")
		.attr("d", diagonal);

	var node = svg.selectAll(".node")
		.data(nodes)
		.enter().append("g")
		.attr("class", "node")
		.attr("transform", function(d) { return "rotate(" + (d.x - 90) + ")translate(" + d.y + ")"; })

	node.append("circle")
		.attr("r", 3);

	node.append("text")
		.attr("dy", ".31em")
		.attr("text-anchor", function(d) { return d.x < 180 ? "start" : "end"; })
		.attr("transform", function(d) { return d.x < 180 ? "translate(8)" : "rotate(180)translate(-8)"; })
		.text(function(d) { return d.name; });

	// d3.select(self.frameElement).style("height", diameter - 150 + "px");
};

function showThread(info) {
	console.log(info);
	var root = makeInfoTree(info);
	console.log(root);
	drawRadialTree(root);
};

